            .filter(name => persistedGlobalStateNames.has(name))
            .forEach(name =>
                newUrl =
                addParamToUrl({
                    "url": newUrl,
                    "name": `${prefix}${name}`,
                    "value": stringify(globalStates[name])
                })
                    .newUrl
            );

        return newUrl;

    }

    const getUnparsedStatesFromUrlSearchParams = memoize0(() => {

        const {
            newUrl,
            values: unparsedStates
