                    const { state } = urlSearchParam;

                    storeStateInPersistentStorage?.(state);

                    return state;

                }

                if (doPersistAcrossReloads) {

                    const serializedState = localStorage.getItem(localStorageKey);

                    if (serializedState !== null) {

